mapper.select:
SELECT * FROM students 


        WHERE name LIKE '%${query}%'


        WHERE ${condition} = #{query}

        SELECT * FROM students WHERE score >= 0

		     AND name LIKE '%${name}%'


		     AND gender = #{gender}


		     AND grade = #{grade}


		     AND score = #{score}

        SELECT * FROM students ORDER BY ${order} 

		     DESC

        LIMIT #{limit} OFFSET #{offset}
